INSERT INTO `content` (`id`, `parentid`, `content_title`, `sort`, `promo_title`, `promo_text`, `content_text`, `image`, `published`, `externurl`) VALUES (45,7,'Noid',97,'NOID','Noid is an Arkanoid clone made on the Android platform. This is my first full Android game using my own DarkFactor Java engine. Noid has around 20 different powerups, 50 levels and 3 different themes. The game was developed between 2008 to 2010 and most of the engine was reused for HexAdventures.\r\n\r\n','<center><img src=\"http://www.darkfactor.net/img/NoidLogo.png\"></center>\r\n<br>\r\nNoid is an Arkanoid clone made on the Android platform. This is my first full Android game using my own DarkFactor Java engine. Noid has around 20 different powerups, 50 levels and 3 different themes. The game was developed between 2008 to 2010 and most of the engine was reused for HexAdventures.\r\nI never manage to push the game tp Google Play but one of these days :)\r\n<br><br>\r\n\r\nI made my own openGL render engine to render everything. That means building my own GUI manager, effect handler, etc on top of the actual game logic.\r\n<img src=\"http://www.darkfactor.net/img/screenshots/noid/Screenshot_2015-03-02-21-26-37.jpg\" width=50%><br>\r\n<br><br><br>\r\n<img src=\"http://www.darkfactor.net/img/screenshots/noid/Screenshot_2015-03-02-21-26-52.jpg\" width=100%><br>\r\n<br><br><br>\r\n<img src=\"http://www.darkfactor.net/img/screenshots/noid/Screenshot_2015-03-02-21-27-52.jpg\" width=100%><br>\r\n<br><br><br>\r\n<img src=\"http://www.darkfactor.net/img/screenshots/noid/Screenshot_2015-03-02-21-27-18.jpg\" width=100%><br>\r\n','/images/Noid/Screenshot_2015-03-02-21-26-52.jpg',0,NULL);
INSERT INTO `content` (`id`, `parentid`, `content_title`, `sort`, `promo_title`, `promo_text`, `content_text`, `image`, `published`, `externurl`) VALUES (46,7,'Valyrian Adventures',96,'Valyrian Adventures','Valyrian Adventures is a strategic dungeon explorer game on Android, iOS, Windows and Mac  where a player can explore dungeons/castles/outdoor areas in the quest for rewards and bragging rights!<br>\r\nÂ¨\r\n\r\n','<img src=\"http://www.darkfactor.net/img/products/ValyrianAdventures/ValyrianAdventures_Logo.png\"><br>\r\n<br>\r\n<table bordersize=0><tr><td width=600>\r\nValyrian Adventures is a strategic dungeon explorer game on Android, iOS, Windows and Mac  where a player can explore dungeons/castles/outdoor areas in the quest for rewards and bragging rights!<br>\r\n<br>A small disclamer. This is a prototype and the graphics is a bit coder art. <br>\r\n</td></tr></table>\r\n<br>\r\n<br>\r\nValyrian Adventures has two seperate parts. The client is made in java for Android and uses Open GLES 1.0. The world itself is 3D objects, but rendered as a top down view.\r\n<br>\r\n<img src=\"http://www.darkfactor.net/img/products/ValyrianAdventures/ValyrianAdventures_01.png\" width=100%><br>\r\n<br>\r\n\r\n','/images/ValyrianAdventures/ValyrianAdventures_01.png',0,NULL);
INSERT INTO `content` (`id`, `parentid`, `content_title`, `sort`, `promo_title`, `promo_text`, `content_text`, `image`, `published`, `externurl`) VALUES (49,126,'Conan Chat',98,'Conan Chat','<div class=block>Welcome to the Conan Chat Client homepage</div>\r\n<br>\r\n<table width=100%><tr>\r\n<td valign=top><img src=\"http://www.darkfactor.net/img/screenshots/conanchat/ChatClient_main.jpg\" width=200 height=150></td>\r\n<td width=5>&nbsp;</td>\r\n<td valign=top><p class=textblock>Conan Chat is a free chatclient for the Age of Conan MMO. The client is a 3rd party product and not developed by Funcom. <BR><BR>The windows and linux project is open source so anyone can peek at the c++ code. I have used <a href=\"http://qt.nokia.com/\">QT by Nokia</a> for the GUI.<BR><BR>The Android client is a closed project for now but will probably be open source once I release it to the public. This client is made in java using the android extention of it.</p><br></td>\r\n<td width=5>&nbsp;</td>\r\n</tr></table>\r\n<br>\r\n<div class=block>Feedback:</div>\r\n<p class=textblock>If you find bugs or want new features, please report them in \r\nthe <a href=\"/index.php?id=10\">feedback</a> form or send a bugreport from the \r\nclient (CTRL+B to open the bug report window).\r\n</p>\r\n\r\n<br>\r\n','<div class=block>Welcome to the Conan Chat Client homepage</div>\r\n<br>\r\n<table width=100%><tr>\r\n<td valign=top><img src=\"http://www.darkfactor.net/img/screenshots/conanchat/ChatClient_main.jpg\" width=200 height=150></td>\r\n<td width=5>&nbsp;</td>\r\n<td valign=top><p class=textblock>Conan Chat is a free chatclient for the Age of Conan MMO. The client is a 3rd party product and not developed by Funcom. <BR><BR>The windows and linux project is open source so anyone can peek at the c++ code. I have used <a href=\"http://qt.nokia.com/\">QT by Nokia</a> for the GUI.<BR><BR>The Android client is a closed project for now but will probably be open source once I release it to the public. This client is made in java using the android extention of it.</p><br></td>\r\n<td width=5>&nbsp;</td>\r\n</tr></table>\r\n<br>\r\n<div class=block>Feedback:</div>\r\n<p class=textblock>If you find bugs or want new features, please report them in \r\nthe <a href=\"/index.php?id=10\">feedback</a> form or send a bugreport from the \r\nclient (CTRL+B to open the bug report window).\r\n</p>\r\n\r\n<br>\r\n',NULL,0,NULL);
